#include <ros/ros.h>
#include <std_msgs/String.h>
#include "RosComm.h"
#include "../component/ComponentMain.h"
#include <string>       // std::string
#include <iostream>     // std::cout
#include <sstream>
#include "ParameterHandler.h"
#include <tf/transform_broadcaster.h>
#include <tf/transform_listener.h>
RosComm::RosComm(ComponentMain* comp,int argc,char** argv)
	: _inited(init(argc, argv)), _comp(comp)
{
	_sub_GlobalPath=ros::Subscriber(_nh.subscribe(fetchParam(&_nh,"PP","GlobalPath","sub"), 10, &RosComm::GlobalPathCallback,this));
	_sub_BladePosition=ros::Subscriber(_nh.subscribe(fetchParam(&_nh,"PP","BladePosition","sub"), 10, &RosComm::BladePositionCallback,this));
	_sub_Map=ros::Subscriber(_nh.subscribe(fetchParam(&_nh,"PP","Map","sub"), 10, &RosComm::MapCallback,this));
	_sub_MiniMap=ros::Subscriber(_nh.subscribe(fetchParam(&_nh,"PP","MiniMap","sub"), 10, &RosComm::MiniMapCallback,this));
	_pub_LocalPath=ros::Publisher(_nh.advertise<config::PP::pub::LocalPath>(fetchParam(&_nh,"PP","LocalPath","pub"),10));
	_pub_diagnostic=ros::Publisher(_nh.advertise<diagnostic_msgs::DiagnosticArray>("/diagnostic",100));
	_maintains.add_thread(new boost::thread(boost::bind(&RosComm::heartbeat,this)));
}
RosComm::~RosComm()
{
}
bool RosComm::init(int argc,char** argv){
	ros::init(argc,argv,"PP_node");
	return true;
}


void RosComm::GlobalPathCallback(const config::PP::sub::GlobalPath::ConstPtr &msg)
{
	_comp->handleGlobalPath(*msg);
}
	

void RosComm::BladePositionCallback(const config::PP::sub::BladePosition::ConstPtr &msg)
{
	_comp->handleBladePosition(*msg);
}
	

void RosComm::MapCallback(const config::PP::sub::Map::ConstPtr &msg)
{
	_comp->handleMap(*msg);
}
	

void RosComm::MiniMapCallback(const config::PP::sub::MiniMap::ConstPtr &msg)
{
	_comp->handleMiniMap(*msg);
}
	

void RosComm::publishLocalPath( config::PP::pub::LocalPath &msg)
{
	_pub_LocalPath.publish(msg);
}
	
void RosComm::publishTransform(const tf::Transform& _tf, std::string srcFrame, std::string distFrame){
	static tf::TransformBroadcaster br;
	br.sendTransform(tf::StampedTransform(_tf, ros::Time::now(), srcFrame, distFrame));
}
tf::StampedTransform RosComm::getLastTrasform(std::string srcFrame, std::string distFrame){
	tf::StampedTransform _tf;
	static tf::TransformListener listener;
	try {
	    listener.waitForTransform(distFrame, srcFrame, ros::Time(0), ros::Duration(10.0) );
	    listener.lookupTransform(distFrame, srcFrame, ros::Time(0), _tf);
	} catch (tf::TransformException& ex) {
	    ROS_ERROR("%s",ex.what());
	}
	return _tf;
}
void RosComm::publishDiagnostic(const diagnostic_msgs::DiagnosticStatus& _report){
	diagnostic_msgs::DiagnosticArray msg;
	msg.status.push_back(_report);
	_pub_diagnostic.publish(msg);
}
void RosComm::publishDiagnostic(const std_msgs::Header& header, const diagnostic_msgs::DiagnosticStatus& _report){
	diagnostic_msgs::DiagnosticArray msg;
	msg.header = header;
	msg.status.push_back(_report);
	_pub_diagnostic.publish(msg);
}
void RosComm::heartbeat(){
	using namespace boost::posix_time;
	ros::Publisher _pub = _nh.advertise<std_msgs::String>("/heartbeat", 10);
	ptime start, stop;
	double hz = HEARTBEAT_FREQUANCY;
	while(ros::ok()){
		start = microsec_clock::local_time();
		std_msgs::String msg;
		msg.data = "PP";
		_pub.publish(msg);
	    time_duration dur = microsec_clock::local_time() - start;
	    long msec = dur.total_milliseconds();
	    boost::this_thread::sleep(milliseconds( long((1/hz)*1000 - msec) ));
	}
}
